CREATE TABLE `tblAreaOperator` (
    `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增id',
    `uid` bigint(20) NOT NULL DEFAULT '0' COMMENT '用户id',
    `name` varchar(255) NOT NULL DEFAULT '' COMMENT '姓名',
    `pinyin` varchar(255) NOT NULL DEFAULT '' COMMENT '全拼',
    `auth_limits` json DEFAULT NULL COMMENT '数据权限范围',
    `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
    `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
    `deleted_at` datetime DEFAULT NULL COMMENT '删除时间',
    `op_uid` bigint(20) NOT NULL DEFAULT '0',
    `op_name` varchar(255) NOT NULL DEFAULT '',
    PRIMARY KEY (`id`),
    KEY `idx_uid` (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8mb4 COMMENT='区域运营表';

